mapper.select:
select * from questions

    type = 'single_choice'
    
        and `difficulty` = #{difficult}
    
    
        and `chapter_id` = #{chapterId}
    

ORDER BY RAND()
LIMIT #{singleChoiceCount}

select * from questions

    type = 'multiple_choice'
    
        and `difficulty` = #{difficult}
    
    
        and `chapter_id` = #{chapterId}
    

ORDER BY RAND()
LIMIT #{multipleChoiceCount}

select * from questions

    type = 'judge'
    
        and `difficulty` = #{difficult}
    
    
        and `chapter_id` = #{chapterId}
    

ORDER BY RAND()
LIMIT #{judgeCount}
