mapper.select:

select * from undetermined_users

    
        `real_name` like concat('%',#{real_name},'%')
    
    
        and `role` = #{role}
    
    
        and `level` = #{level}
    
    
        and `class_id` = #{class_id}
    

limit #{offset}, #{pageSize}

select count(*) from undetermined_users

    
        `real_name` like concat('%',#{real_name},'%')
    
    
        and `role` = #{role}
    
    
        and `level` = #{level}
    
    
        and `class_id` = #{class_id}
